export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "정석만",
      },
      content: "첫번째 게시글 #해시태그 #익스프레스",
      Images: [
        {
          src: `https://dimg.donga.com/ugc/CDB/WOMAN/Article/5b/8f/65/69/5b8f656903c9d2738de6.jpg`,
        },
        {
          src: `https://cphoto.asiae.co.kr/listimglink/6/2020111713594015534_1605589180.png`,
        },
        {
          src: `http://www.news33.net/news/photo/202112/79972_42841_3543.jpg`,
        },
      ],
      Comments: [
        {
          User: {
            nickname: "석만",
          },
          content: "우와 개정판이 나왔군요~",
        },
        {
          User: {
            nickname: "명재",
          },
          content: "얼른 사고싶어요~",
        },
      ],
    },
  ],
  imagePaths: [],
  postAddEd: false,
};

const ADD_POST = "ADD_POST";

export const addPost = {
  type: ADD_POST,
};

const dummy = {
  id: 2,
  content: "더미데이터입니다.",
  User: {
    id: 1,
    nickname: "정석만",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default reducer;
